import React, {useEffect, useState} from 'react';
import api from '../services/api';
import { Container, Grid, Typography } from '@mui/material';
import DashboardCard from '../components/DashboardCard';

export default function Dashboard(){
  const [health, setHealth] = useState('...');
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState([]);

  useEffect(()=>{
    api.get('/api/v1/health')
      .then(r=>setHealth(r.data.status))
      .catch(()=>setHealth('unreachable'))

    api.get('/api/v1/students')
      .then(r=> setStudents(r.data || []))
      .catch(()=> setStudents([]))

    api.get('/api/v1/attendance')
      .then(r=> setAttendance(r.data || []))
      .catch(()=> setAttendance([]))
  },[])

  const studentsCount = Array.isArray(students) ? students.length : '--';
  let attendancePct = '--';
  if(Array.isArray(attendance) && studentsCount && studentsCount > 0){
    attendancePct = Math.round((attendance.length / Math.max(1, studentsCount)) * 100) + '%';
  }

  return (
    <Container sx={{mt:3}}>
      <Typography variant="h4" sx={{mb:2}}>Dashboard</Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <DashboardCard title="Backend Health" value={health} subtitle="API status" accent="#1976d2" />
        </Grid>
        <Grid item xs={12} md={4}>
          <DashboardCard title="Total Students" value={studentsCount} subtitle="Registered students" accent="#00acc1" />
        </Grid>
        <Grid item xs={12} md={4}>
          <DashboardCard title="Today's Attendance" value={attendancePct} subtitle={`${attendance.length || 0} records`} accent="#2e7d32" />
        </Grid>

        {/* Larger area with raw data preview */}
        <Grid item xs={12} md={8}>
          <Typography variant="h6" sx={{mb:1}}>Recent attendance</Typography>
          <pre style={{background:'#fff', padding:12, borderRadius:8, boxShadow:'0 6px 20px rgba(15,23,42,0.04)'}}>{JSON.stringify(attendance.slice(0,10), null, 2)}</pre>
        </Grid>
        <Grid item xs={12} md={4}>
          <Typography variant="h6" sx={{mb:1}}>Quick actions</Typography>
          <div style={{background:'#fff', padding:12, borderRadius:8, boxShadow:'0 6px 20px rgba(15,23,42,0.04)'}}>
            <p style={{margin:0}}>• Use the Students page to view full list.</p>
            <p style={{marginTop:8}}>• Use Attendance for details and exports.</p>
          </div>
        </Grid>
      </Grid>
    </Container>
  )
}
