import React from 'react';
import { Paper, Box, Typography } from '@mui/material';

export default function DashboardCard({ title, value, subtitle, accent }){
  return (
    <Paper elevation={2} sx={{height: '100%'}}>
      <Box sx={{display:'flex', alignItems:'center', justifyContent:'space-between'}}>
        <Box>
          <Typography variant="h6" sx={{color: 'text.primary'}}>{title}</Typography>
          <Typography variant="h4" sx={{mt:1, fontWeight:700}}>{value}</Typography>
          {subtitle && <Typography variant="body2" sx={{mt:1, color: 'text.secondary'}}>{subtitle}</Typography>}
        </Box>
        <Box sx={{width:64, height:64, borderRadius:2, bgcolor: accent || 'primary.main', display:'flex', alignItems:'center', justifyContent:'center', color:'#fff', ml:2}}>
          {/* simple glyph placeholder */}
          <Typography variant="h6" sx={{fontWeight:700}}>{String(title || '?').charAt(0)}</Typography>
        </Box>
      </Box>
    </Paper>
  )
}
